mapper.insert:
INSERT
INTO TBL_MEMBER
( MEMBER_NO
, MEMBER_ID
, MEMBER_PWD
, MEMBER_NAME
, NICKNAME
, PHONE
, GENDER
, ADDRESS
, MEMBER_STAT
, MEMBER_STAT_DATE
, REGIST_DATE
, SIGNUP_PATH_CODE)
VALUES ( SEQ_TBL_MEMBER_NO.NEXTVAL
       , #{ memberId }
       , #{ memberPwd }
       , #{ memberName }
       , #{ nickName }
       , #{ phone }
       , #{ gender }
       , #{ address }
       , #{ memberStat }
       , #{ memberStatDate }
       , #{ registDate }
       , #{ signupPathCode })

mapper.select:
SELECT
    MEMBER_ID
FROM TBL_MEMBER
WHERE MEMBER_NAME = #{memberName}
  AND PHONE = #{phone}

SELECT
       *
  FROM TBL_MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND MEMBER_NAME = #{memberName}

mapper.update:
UPDATE
       TBL_MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}

UPDATE
    TBL_MEMBER
SET NICKNAME = #{ nickName }
  , POST_NO  = #{ postNo }
  , ADDRESS  = #{ address }
  , ADDRESS2 = #{ address2 }
WHERE MEMBER_STAT = '활동'
  AND MEMBER_NO = #{ memberNo }

UPDATE
    TBL_MEMBER
 SET MEMBER_PWD = #{ memberPwd }
WHERE MEMBER_STAT = '활동'
AND MEMBER_NO = #{ memberNo }
